mapper.select:
select COUNT(*) from annexe


        and create_time >#{time}


        and status=#{status}

select result.d as date, SUM(result.c) as count from ( select DATE_FORMAT(a.create_time,#{format}) as d,COUNT(a.id) as c from annexe a where a.type=#{type} and a.create_time>=#{startTime} group by d
UNION (SELECT DATE_FORMAT(#{item},#{format}), 0)
) as result group by date order by date

select COUNT(*) FROM annexe a left join atask_ann at on a.id=at.aid left join annexe_task atask on at.atid=atask.id
where
atask.uid=#{userid} and a.create_time>#{time}
